--Products (ProductID, ProductName, Price)--PriceHistory (HistoryID, ProductID, OldPrice, NewPrice, ChangeDate)CREATE DATABASE demotriggerUSE demotriggerCREATE TABLE Products(    ProductID INT PRIMARY KEY,    ProductName VARCHAR(100),    Price DECIMAL(10, 0)); CREATE TABLE PriceHistory(    HistoryID INT IDENTITY(1,1) PRIMARY KEY,    ProductID INT,    OldPrice DECIMAL(10, 0),    NewPrice DECIMAL(10, 0),    ChangeDate DATETIME DEFAULT GETDATE());INSERT INTO Products VALUES (1,'Laptop', 1000),(2,'TV', 550);